import React from 'react';
import { GoogleLogin, useGoogleLogin } from '@react-oauth/google';
import { Link, Navigate, useLocation } from 'react-router-dom';
const CLIENT_ID = '716338759481-3t52ihgb9pa4ifk96mjnlehh8c0idr07.apps.googleusercontent.com';

const Login = () => {
  const location = useLocation();
  const emailFromLogin = location.state?.email
  const googleLogin  = useGoogleLogin({
    onSuccess: (res) => {
      
      const searchParams = new URLSearchParams(location.search);
      const code = searchParams.get('code');
      // Navigate('/oauth/callback', { state: { emailFromLogin, code: code } });
    },
    onFailure: (error) => {
      console.log("Error logging in with Google:", error);
    },
    flow: 'authCode', // Corrected from 'auth-code' to 'authCode'
    scope: 'https://www.googleapis.com/auth/contacts.readonly',
    clientId: CLIENT_ID, // Assuming CLIENT_ID is defined somewhere
    redirectUri: 'http://localhost:3000/login'
  });

  return (
    <div className="list-group">
      <Link to="/contacts" className="list-group-item list-group-item-action ">
        Friends
      </Link>
      <Link to="#" className="list-group-item list-group-item-action">
        Groups
      </Link>
      <button className="list-group-item list-group-item-action" onClick={() => googleLogin()}>
      Sign in with Google to import your Contacts
    </button>
      {/* <GoogleLogin
        render={renderProps}
