import React from 'react';
import { GoogleLogin, useGoogleLogin } from '@react-oauth/google';
import { Link, Navigate, useLocation } from 'react-router-dom';
import axios from 'axios';
const CLIENT_ID = '716338759481-3t52ihgb9pa4ifk96mjnlehh8c0idr07.apps.googleusercontent.com';
const clientSecret = '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';

const Login = () => {
  const location = useLocation();
  const emailFromLogin = location.state?.email
  const googleLogin  = useGoogleLogin({
    onSuccess: async (res) => {

      const code = res.code;
      console.log(code,emailFromLogin);
      try{
        const response = await axios.post('https://oauth2.googleapis.com/token', {
          code,
          client_id: CLIENT_ID,
          client_secret: clientSecret,
          redirect_uri: 'http://localhost:3000/logoauthin',
          grant_type: 'authorization_code',
      });
  
      const { access_token } = response.data;
      console.log(access_token,"Acess token");
       await axios.post('http://localhost:3001/oauth2callback', { codes:access_token,email:emailFromLogin });
       Navigate('/contact',{state:{emailFromLogin}})

      }
      catch(error){
        console.log(error);
      }

    },
    onFailure: (error) => {
      console.log("Error logging in with Google:", error);
    },
    flow: 'authCode', // Corrected from 'auth-code' to 'authCode'
    scope: 'https://www.googleapis.com/auth/contacts.readonly',
    clientId: CLIENT_ID, // Assuming CLIENT_ID is defined somewhere
    redirectUri: 'http://localhost:3000/login'
  });

  return (
    <div className="list-group">
      <Link to="/contacts" className="list-group-item list-group-item-action ">
        Friends
      </Link>
      <Link to="#" className="list-group-item list-group-item-action">
        Groups
      </Link>
      <button className="list-group-item list-group-item-action" onClick={() => googleLogin()}>
      Sign in with Google to import your Contacts
    </button>
      {/* <GoogleLogin
        render={renderProps}
        buttonText="Sign with Google to Import Contacts"
        onSuccess={onSuccess}
        onFailure={onFailure}
        scope="https://www.googleapis.com/auth/contacts.readonly"
      /> */}
    </div>
  );
};

export default Login;
